export const POD = ({ setDefaultFileList, defaultFileList }) => {
  const uploadSampleFile = async (info) => {
    let fileList = info.fileList;
    if (fileList.length !== 0) {
      fileList = fileList.slice(-1);
      setDefaultFileList([fileList[0]]);
    }
    if (fileList.length === 0) {
      setDefaultFileList([]);
    }
    const { file } = info;
    if (file.status === 'done') {
      if (file.response && !file.response.isError) {
        message.success(`${file.name} file uploaded successfully`);
      } else if (file.response && file.response.isError) {
        message.error(`${file.name} file upload failed`);
      }
    } else if (file.status === 'error') {
      message.error(`${file.name} file upload failed.`);
    }
  };

  return (
    <>
      <div>
        <p style={{ marginTop: 20 }}>
          Upload proof of delivery for the order to be marked as delivered
        </p>
        <Dragger
          accept="image/png, image/jpeg"
          onChange={uploadSampleFile}
          name={'podPhoto'}
          withCredentials={true}
          multiple={true}
          fileList={defaultFileList}>
          <img src="/images/upload.png" alt="upload" />
          <p style={{ color: '#05c2cf' }}>Upload proof of delivery</p>
        </Dragger>
      </div>
    </>
  );
};

