const ColumnHeader = ({ left, right, height, children }: ColumnHeaderPropType) => {
  let body;
  if (left && right) {
    body = (
      <Row justify="space-between" align="middle" style={{ height }}>
        <Col>{left}</Col>
        <Col>{right}</Col>
      </Row>
    );
  } else {
    body = children;
  }

  return <Card>{body}</Card>;
};

const Confirm4PLPartialDeliveryModal: React.FC<Confirm4PLPartialDeliveryModalProps> = ({
  state,
  onCancel,
  onConfirm,
}) => {
  const [partialAmount, setPartialAmount] = React.useState('');

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;

    if (value === '' || TYPING_REGEX.test(value)) {
      setPartialAmount(value);
    }
  };

  const isValidAmount =
    partialAmount !== '' && VALID_REGEX.test(partialAmount) && Number(partialAmount) > 0;

  const handleConfirm = () => {
    if (!isValidAmount) return;

    onConfirm({
      trackingId: state.data.trackingId,
      partialAmount: Number(partialAmount),
    });

    setPartialAmount('');
  };

  const handleCancel = () => {
    setPartialAmount('');
    onCancel();
  };

  return (
    <Modal
      visible={state.isOpen}
      title="Confirm Partial Delivery"
      onOk={handleConfirm}
      onCancel={handleCancel}
      okText="Confirm"
      cancelText="Cancel"
      confirmLoading={state.data.loading}
      okButtonProps={{ disabled: !isValidAmount }}
      destroyOnClose
      centered
      data-testid="Confirm4PLPartialDelivery_Modal">
      <Input
        placeholder="Enter amount"
        value={partialAmount}
        onChange={handleChange}
        inputMode="decimal"
        data-testid="Confirm4PLPartialDelivery_PartialInput"
      />
    </Modal>
  );
};

const ConfirmActionModal: React.FC<ConfirmActionModalProps> = ({
