    </Modal>
  );
};

export const consignmentOtpModalReducer = (state, action) => {
  switch (action.type) {
    case 'OTP_VERIFY_DISABLE':
      return { ...state, disableVerify: true };
    case 'OTP_VERIFY_ENABLE':
      return { ...state, disableVerify: false };
    case 'RESET_OTP':
      return { ...state, otp: resetOtpInitialArray(6) };
    case 'OTP_CHANGE':
      return { ...state, otp: action.otp };
    default:
      return state;
  }
};

