  const initialConsignmentConfirmation = {
    otp: resetOtpInitialArray(6),
    disableVerify: false,
  };

  const [changedConsignmentConfirmation, otpDispatch] = useReducer(
    consignmentOtpModalReducer,
    initialConsignmentConfirmation,
  );
  const { disableVerify, otp } = changedConsignmentConfirmation;

  const resendOtp = () => {
    dispatchConsignment(selectedConsignment);
  };

  const onClose = () => {
    otpDispatch({ type: 'OTP_VERIFY_ENABLE' });
    otpDispatch({ type: 'RESET_OTP' });
    setRemainingTime(OTP_TIME);
    c2cDispatch({ type: 'CLOSE_CONSIGNMENT_OTP_MODAL' });
  };

  const verifyOtp = async () => {
    otpDispatch({ type: 'OTP_VERIFY_DISABLE' });
    try {
      const verifyRes = await courseLogisticsAPI.verifyC2cDelivery({
        consignmentId: selectedConsignment[0].consignmentId,
        code: otp.join(''),
        hash: consignmentOtpHash,
      });
      if (!verifyRes.isError) {
        removeConsignment(selectedConsignment);
        onClose();
      }
    } catch (err) {
      message.error(err?.error || 'Something went wrong');
    }
    otpDispatch({ type: 'OTP_VERIFY_ENABLE' });
  };

  const otpChange = (otp) => {
    otpDispatch({ type: 'OTP_CHANGE', otp: otp });
  };

  const otpDisabled = otp?.join('') === '' || otp?.join('').length < 6 || disableVerify;
  return (
    <Modal
