      destroyOnClose
      footer={null}>
      <OTPVerify
        key={`${selectedConsignment[0]}`}
        parcelConfirmation={selectedConsignment[0]}
        triggerOtpAction={resendOtp}
        setOtp={otpChange}
        otp={otp}
        setRemainingTime={setRemainingTime}
        remainingTime={remainingTime}
        otpLength={6}
        OTP_TIME={OTP_TIME}
      />
