export const c2cReducer = (state, action) => {
  switch (action.type) {
    case 'SELECT_CONSIGNMENT':
      return { ...state, selectedConsignment: action.selectedConsignment };
    case 'OPEN_CONSIGNMENT_OTP_MODAL':
      return { ...state, consignmentOtpModal: true };
    case 'CLOSE_CONSIGNMENT_OTP_MODAL':
      return { ...state, consignmentOtpModal: false };
    case 'SET_CONSIGNMENT_ID':
      return { ...state, consignmentId: action.consignmentId };
    case 'RESET_CONSIGNMENT_ID':
      return { ...state, consignmentId: '' };
    default:
      return state;
  }
};

export const getfilteredValidData = (list: LogisticsParcelsType[], scanInput: string) => {
  return list.filter(
    (item) =>
      +item.consignmentId === +scanInput || item.consignmentInvoiceNumber === scanInput,
  );
};

