  });

  const triggerSubmit = (requestedBy: string) => {
    const timeDate = new Date(date);
    const deliveryDate = timeDate.getTime();

    let holdParcelPayload: HoldParcelPayloadType = {
      requestedBy: requestedBy,
      currentStatus: 'on-hold',
      id: parcel.trackingId,
      oldStatus: parcel.status,
      deliveryDate: deliveryDate,
      remarks: reason ? holdParcelReasonOptions[reason - 1]?.name : '',
