function LineHaulDispatchBulkParcels(props: LineHaulDispatchBulkParcelsPropsType) {
  const { name, count, endHubId, startHubId, parcelTypes } = props;
  const [isModalOpen, setIsModalOpen] = useState(false);
  const dispatch = useDispatch();

  const openModal = useCallback(() => setIsModalOpen(true), []);
  const closeModal = useCallback(() => setIsModalOpen(false), []);

  const { appPreference } = useSelector((state: any) => state.appPreference);
  const enableDecentralisation = appPreference.enableDecentralizaionFlow;

  const { bulks } = useSelector((state: any) => state.dispatchToLineHaulReducer);

  useEffect(() => {
    if (isModalOpen) {
      dispatch(
        getBulksThunk(
          getBulksParams(enableDecentralisation, startHubId, endHubId, parcelTypes),
        ),
      );
    }
  }, [endHubId, startHubId, isModalOpen, enableDecentralisation, dispatch]);

  return (
    <div css={bulk_parcels_css}>
      {name && (
        <Button onClick={openModal} css={hub_button_css}>
          {name} - {count} bulk{count > 1 ? 's' : ''}
        </Button>
      )}
      <LineHaulBulkParcelsModal
        isOpen={isModalOpen}
        onClose={closeModal}
        bulks={bulks}
        name={name}
      />
    </div>
  );
}

