export function LineHaulBulkParcelsModal(props: LineHaulBulkParcelsModalPropsType) {
  const {
    isOpen,
    name,
    onClose,
    bulks,
    unScanBulk,
    agentId,
    isGroupView,
    title,
    confirmMoveRight,
    groupViewMoveBack,
  } = props;

  const [selectedIds, setSelectedIds] = useState<string[]>([]);
  const [allSelected, setAllSelected] = useState(false);
  const [scanInput, setScanInput] = useState('');

  const sounds = useSounds();

  const handleClose = () => {
    setSelectedIds([]);
    setAllSelected(false);
    onClose();
    setScanInput('');
  };

  const handleSelectAllChange = (e: any) => {
    if (e.target.checked) {
      setSelectedIds(bulks.map((bulk) => bulk.bulkTransferId));
      setAllSelected(true);
      return;
    }
    setSelectedIds([]);
    setAllSelected(false);
  };

  const handleSingleSelect = (e: any, bulkTransferId: string) => {
    if (e.target.checked) {
      const updatedBulkIds = [...selectedIds, bulkTransferId];
      if (updatedBulkIds.length === bulks.length) setAllSelected(true);
      setSelectedIds(updatedBulkIds);
      return;
    }
    setAllSelected(false);
    setSelectedIds((prev) => prev.filter((bulkId) => bulkId !== bulkTransferId));
  };

  const handleConfirm = () => {
    if (!confirmMoveRight) return;
    confirmMoveRight(selectedIds);
    setSelectedIds([]);
    setScanInput('');
  };

  const handleGroupViewUnscan = (agentId: number, bulk: SingleBulkType) => {
    if (!unScanBulk) return;
    unScanBulk(agentId, bulk);
    setSelectedIds((prev) => prev.filter((bulkId) => bulkId !== bulk.bulkTransferId));
  };

  const handleScanInputChange = (e: any) => {
    setScanInput(e.target.value);
  };

  const handleScanBulk = () => {
    if (!bulks.length) return;

    const scannedBulk = bulks.find((bulk) => {
      const parentBulkId = bulk.secondaryBulkTransferId?.split(',')[0];
      return (
        bulk.bulkTransferId === scanInput || (parentBulkId && parentBulkId === scanInput)
      );
    });

    if (!scannedBulk) {
      message.error('Could not find bulk');
      sounds.play('error');
      return;
    }

