export const ParcelCategoryChecker = (props: ParcelCategoryCheckerPropsType) => {
  const { parcelTypes, setParcelTypes } = props;

  const isC2cChecked = parcelTypes.includes(KEY_C2C);
  const is3plChecked = parcelTypes.includes(KEY_3PL);

  const handleTypeChange = (e: CheckboxChangeEvent) => {
    const value = e.target.value;
    if (parcelTypes.includes(value)) {
      setParcelTypes(parcelTypes.filter((type) => type !== value));
    } else {
      setParcelTypes([...parcelTypes, value]);
    }
  };

  return (
    <div style={checkerWrapperStyles}>
      <Checkbox value={KEY_C2C} checked={isC2cChecked} onChange={handleTypeChange}>
        Consignments
      </Checkbox>
      <Checkbox value={KEY_3PL} checked={is3plChecked} onChange={handleTypeChange}>
        Bulks
      </Checkbox>
    </div>
  );
};

