export function RouteFleetMapping({
  routeId,
  fleetRouteMapping,
}: RouteFleetMappingProps) {
  return (
    <Space direction="horizontal">
      {fleetRouteMapping[routeId] && (
        <Space direction="horizontal" size="small" wrap>
          {fleetRouteMapping[routeId].map((fleet, index) => (
            <div key={fleet.truckNumber}>
              <Typography.Text
                data-testid="routeMgmttruckdisplay"
                style={{
                  color: ['active'].includes(fleet.status) ? 'red' : 'black',
                }}>{`${fleet.truckNumber}`}</Typography.Text>
              {['lease-cancel', 'repair'].includes(fleet.status) && (
                <Tooltip
                  data-testid="routeMgmtTooltip"
                  title={`The vehicle is in ${fleet.status} status`}>
                  <InfoCircleOutlined />
                </Tooltip>
              )}
              {index < fleetRouteMapping[routeId].length - 1 && ', '}
            </div>
          ))}
        </Space>
      )}
      {!fleetRouteMapping[routeId] && (
        <Typography.Text data-testid="routeMgmtNa">N/A</Typography.Text>
      )}
    </Space>
  );
}

