function ParcelInvoiceInfo({ trackingId }) {
  const [invoiceInfoData, setInvoiceInfoData] = useState();

  useEffect(() => {
    async function fetchData() {
      try {
        const data = await parcelAPI.getParcelInvoiceInfo(trackingId);
        setInvoiceInfoData(data.invoiceInfo);
        console.log(data);
      } catch {
        /* */
      }
    }
    fetchData();
  }, [trackingId]);

  const data = useMemo(() => {
    if (invoiceInfoData) {
      const { adjustmentInvoiceInfo, regularInvoiceInfo } = invoiceInfoData;
      return prepareInvoiceInfo({ adjustmentInvoiceInfo, regularInvoiceInfo });
    }
    return [];
  }, [invoiceInfoData]);

  if (!data.length) return null;

  return (
    <Table
      rowKey={(item) => item.key.toString()}
      dataSource={data}
      columns={columns}
      pagination={false}
    />
  );
}

