  currentParcelStatus,
}) => {
  const [selectedOption, setSelectedOption] = useState(null);

  const [exchangeParcelInfo, setExchangeParcelInfo] = useState(defaultExchangeParcelInfo);
  const [isErrorMessageVisible, setIsErrorMessageVisible] = useState(false);
  const [isInvalidNumberMessageVisible, setisInvalidNumberMessageVisible] = useState(
    false,
  );

  const updateFormValue = ({ key, value }) => {
    isErrorMessageVisible && setIsErrorMessageVisible(false);
    isInvalidNumberMessageVisible && setisInvalidNumberMessageVisible(false);

    if (key === 'amount') {
      const inputNumber = parseInt(value, 10);
      if (Number.isNaN(inputNumber) || inputNumber < 0) {
        setisInvalidNumberMessageVisible(true);
      }
    }

    setExchangeParcelInfo({
      ...exchangeParcelInfo,
      [key]: value,
    });
  };

  const handleCancel = () => {
    setExchangeParcelInfo(defaultExchangeParcelInfo);
    onCancel();
  };

  return (
    <Modal
      visible={visible}
      title="Exchange Delivery"
      onCancel={handleCancel}
      footer={[
        <Button key="back" onClick={handleCancel}>
          Cancel
        </Button>,
        <Button
          key="submit"
          type="primary"
          loading={exchangeParcelReq === 'pending'}
          onClick={() => {
            if (
              (selectedOption === 'newExchangeParcel' &&
                (!exchangeParcelInfo.parcelId ||
                  parseInt(exchangeParcelInfo.amount) < 0)) ||
              (selectedOption === 'withDeliveryParcelId' &&
                (!exchangeParcelInfo.newParcelId || !exchangeParcelInfo.parcelId)) ||
              (selectedOption === 'withoutDeliveryParcelId' &&
                !exchangeParcelInfo.newParcelId)
