function UpdateWeight({ isOpen, closeModal, data, successCallback }) {
  const [isUpdating, setUpdating] = useState(false);

  const [form] = Form.useForm();

  const onCancel = () => {
    form.resetFields();
    closeModal();
  };

  const updateParcelWeight = async ({ weight }) => {
    setUpdating(true);
    try {
      const { areaId, cash, trackingId, shopId } = data;
      await areaAPI.updateAreaWeightV2({
        areaId,
        cash,
        trackingId,
        shopId,
        weight,
      });

      setTimeout(() => {
        successCallback();
      }, 150);

      onCancel();
    } catch (error) {
      message.error(error.message || 'Something went wrong on the server');
    }

    setUpdating(false);
  };

  return (
    <Modal
      visible={isOpen}
      title="Update Weight"
      onCancel={onCancel}
      footer={[
        <Button key="back" onClick={onCancel}>
          Cancel
        </Button>,
        <Button key="submit" type="primary" loading={isUpdating} onClick={form.submit}>
          Update
        </Button>,
      ]}>
      <Form
        form={form}
        initialValues={{ weight: data.weight }}
        name="updateWeight"
        onFinish={updateParcelWeight}>
        <Form.Item
          name="weight"
          extra="Weight must be given in grams (gm)"
          label="Parcel Weight"
          rules={[
            {
              required: true,
              validator: validateWeight,
              min: MIN_WEIGHT,
              max: MAX_WEIGHT,
            },
          ]}>
          <Input style={{ width: '80%' }} placeholder="Weight" addonAfter="gm" />
        </Form.Item>
      </Form>
    </Modal>
  );
}

export function pickupParcelsReducer(
  state: PickupBmInitialStateType,
  action: PickupBmActionType,
) {
  switch (action.type) {
    case 'UPDATE_SHOP_DETAILS':
      return {
        ...state,
        shopDetails: action.payload,
      };

    case 'ADD_SCANNED_PARCEL':
      return {
        ...state,
        scanned: [...state.scanned, action.payload],
      };
    case 'UPDATE_SCANNED_PARCELS':
      return {
        ...state,
        scanned: action.payload.parcels,
        config: action.payload.config || {},
      };
    case 'RESET_SCANNED_PARCELS':
      return {
        ...state,
        scanned: [],
      };
    case 'ADD_UNSCANNED_PARCEL':
      return {
        ...state,
        unscanned: [...state.unscanned, action.payload],
      };
    case 'UPDATE_UNSCANNED_PARCELS':
      return {
        ...state,
        unscanned: action.payload.parcels,
        config: action.payload.config || {},
      };
    case 'RESET_UNSCANNED_PARCELS':
      return {
        ...state,
        unscanned: [],
      };
    default:
      throw new Error();
  }
}

export function bulkModalReducer(
  state: BulkModalInitialStateType,
  action: PickupBmActionType,
) {
  switch (action.type) {
    case 'SHOW_MODAL':
      return {
        ...state,
        visible: true,
        fileList: [],
      };
    case 'HIDE_MODAL': {
      return {
        ...state,
        visible: false,
      };
    }
    case 'RESET_MODAL_STATE':
      return {
        ...state,
        visible: false,
        fetchingParcels: false,
        uploadingFile: false,
      };
    case 'PARCLES_FETCHING_PENDING':
      return {
        ...state,
        fetchingParcels: true,
      };
    case 'PARCLES_FETCHING_COMPLETE':
      return {
        ...state,
        fetchingParcels: false,
      };
    case 'FILE_UPLOAD_PENDING': {
      return {
        ...state,
        uploadingFile: true,
      };
    }
    case 'FILE_UPLOAD_COMPLETE': {
      return {
        ...state,
        uploadingFile: false,
      };
    }
    case 'UPDATE_FILELIST': {
      return {
        ...state,
        fileList: action.payload,
      };
    }
    default:
      throw new Error();
  }
}

