const AssignAgentsModal = ({
  visible,
  setAgentModal,
  makeOptions,
  agentsByHub,
  selectedRows,
  setUpdateData,
}) => {
  const [agentId, setAgentId] = useState();
  const change = () => {
    setAgentModal(false);
    setAgentId();
  };
  const assignAgent = async () => {
    try {
      const promises = [];
      for (var i = 0; i < selectedRows.length; i++) {
        const row = selectedRows[i];
        promises.push(
          ParcelService.updateAgent({
            agentId: agentId,
            pickupType: row.PICKUP_TYPE,
            shopId: row.SHOP_ID,
            shopStoreId: row.SHOP_STORE_ID,
          }),
        );
      }
      await Promise.all(promises);
    } catch (error) {
      message.error('Problem ', error);
      return;
    }
    setUpdateData(true);
    setAgentModal(false);
    setAgentId();
  };
  const ModalFooter = () => {
    return (
      <>
        <Button size="small" onClick={change}>
          Cancel
        </Button>
        <Button type="primary" size="small" onClick={assignAgent}>
          Confirm
        </Button>
      </>
    );
  };
  return (
    <Modal
      title={'Assign Pickup Requests'}
      visible={visible}
      closable={false}
      footer={<ModalFooter />}>
      <Space size={12} style={{ marginTop: 10 }}>
        <Select
          style={{ width: 190 }}
          placeholder="Agent name"
