export default function ConfirmPodImageReuploadeModal({
  modalState,
  closeModal,
  afterImageUpload,
}: ConfirmPodImageReuploadeModalPropsInterface) {
  const handleImageUpload = async ({ file, onSuccess, onError }: any) => {
    try {
      const formData = new FormData();
      formData.append('pod-proof-upload', file);
      formData.append('parcelTrackingId', modalState.data.trackingId);
      formData.append('uploadType', 'reupload');

      const res = await podAPI.uploadImage(formData);

      onSuccess?.(res);
      afterImageUpload(modalState.data.trackingId);
    } catch (err: any) {
      message.error(err.message || 'Could not upload image');
      onError?.(err);
    }
  };

  return (
    <Modal
      title={<span className="title">Do you want to change the existing image?</span>}
      css={modal_css}
      visible={modalState.isOpen}
      footer={[
        <Button
          className="cancel-btn"
          key={`${modalState.data.trackingId}_cancel`}
          onClick={closeModal}
          data-testid="ConfirmNewImageUploadModal_CancelBtn">
          Cancel
        </Button>,
        <Upload
          key={`${modalState.data.trackingId}_upload`}
          accept="image/*"
          showUploadList={false}
          customRequest={handleImageUpload}
          data={{ trackingId: modalState.data.trackingId }}
