function PartialCollectionBreakdown({ invoiceId, invoiceAmount, amount, isDeductible }) {
  const [breakdown, setBreakdown] = useState(initialBreakdownState);

  const [isModalOpen, setIsModalOpen] = useState(false);

  const toggleModal = (state) => setIsModalOpen(state);

  useEffect(() => {
    async function fetchSummary() {
      try {
        setBreakdown(initialBreakdownState);

        const data = await postpaidInvoicesApi.getPostpaidCollectionSummary({
          postpaidInvoiceId: invoiceId,
          enableCollection: true,
          enableSubsidiary: true,
        });

        setBreakdown({
          data: isDeductible ? data.subsidiary : data.collection,
          isFetching: false,
        });
      } catch (error) {
        setBreakdown({ data: [], isFetching: false });
        message.error(error?.message || 'Something went wrong.');
      }
    }

    if (isModalOpen) {
      fetchSummary();
    }
  }, [invoiceId, isModalOpen, isDeductible]);

  const columns = useMemo(() => {
    return isDeductible ? subsidiaryColumns : collectionColumns;
  }, [isDeductible]);

  return (
    <>
      <Button
        onClick={() => toggleModal(true)}
        type="link"
        data-testid="partial-collection-breakdown">
        Details
      </Button>
      <Modal
        title={`${isDeductible ? 'Deductible amount' : 'Partial collection'} breakdown`}
        footer={false}
        onCancel={() => toggleModal(false)}
        visible={isModalOpen}
        width={800}
        data-testid="partial-collection-breakdown-modal">
        <Table
          loading={breakdown.isFetching}
          title={() => `Invoice amount: Tk. ${invoiceAmount}`}
          dataSource={breakdown.data}
          columns={columns}
          rowKey={(item) => item.ID?.toString?.()}
          pagination={false}
          summary={() => {
            return (
              <Table.Summary.Row>
                <Table.Summary.Cell colSpan={isDeductible ? 2 : 4}>
                  Total {!isDeductible ? 'partial collection' : 'deductibles'}
                </Table.Summary.Cell>
                <Table.Summary.Cell>
                  <Text strong>Tk. {amount}</Text>
                </Table.Summary.Cell>
              </Table.Summary.Row>
            );
          }}
          data-testid="partial-collection-breakdown-table"
        />
      </Modal>
    </>
  );
}

