function PostpaidActions({
  invoiceId,
  isSettled,
  fetchData,
  invoiceAmount,
  collectedAmount,
  deductionAmount,
  remainingAmount,
  walletBalance,
  uploadToHulkEnabled,
}) {
  const [isUploading, setIsUploading] = useState(false);
  const [uploadToHulkState, uploadToHulkdispatch] = useReducer(
    uploadToHulkReducer,
    UPLOAD_TO_HULK_INITIAL_STATE,
  );

  const uploadToHulk = async (file) => {
    console.log('file', file);
    uploadToHulkdispatch({ type: UPLOAD_TO_HULK_PENDING });

    try {
      const uploadUrlResponse = await UploadSerive.getHulkFileUploadUrl(
        uploadToHulkState.uploadType,
      );

      if (uploadUrlResponse.data.is_error) {
        throw new Error(uploadUrlResponse.data.message || CUSTOM_ERROR_MESSAGE);
      }

      const gcsUploadResponse = await fetch(uploadUrlResponse.data.url, {
        method: 'PUT',
        headers: {
          'content-type':
            'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
          origin: '<scheme>://<hostname>',
        },
        body: file,
      });

      console.log('gcsUploadResponse', gcsUploadResponse);

      const confirmToHulkResponse = await UploadSerive.confirmHulkFileUpload({
        path: uploadUrlResponse.data.path,
        upload_type: uploadToHulkState.uploadType,
      });

      console.log('confirmToHulkResponse', confirmToHulkResponse);

      if (confirmToHulkResponse.data.is_error) {
        throw new Error(confirmToHulkResponse.data.message || CUSTOM_ERROR_MESSAGE);
      }

      uploadToHulkdispatch({ type: UPLOAD_TO_HULK_FULFILLED });
    } catch (error) {
      uploadToHulkdispatch({ type: UPLOAD_TO_HULK_REJECTED });
      message.error(error.message || CUSTOM_ERROR_MESSAGE);
    }
  };

  const uploaderProps = {
    name: 'file',
    action: getUploadingUrl(invoiceId),
    accept: '.xls, .xlsx, .csv',
    withCredentials: true,
    maxCount: 1,
    showUploadList: false,
    onChange(info) {
      if (info.file.status === 'uploading') {
        setIsUploading(true);
        return;
      }

      if (!info.file.response?.isError) {
        message.success(`File uploaded successfully`);
        fetchData();
      } else if (info.file.response?.isError) {
        message.error(info.file.response?.message || `File upload failed.`);
      }
      setIsUploading(false);

