function RemoveProduct({ parcel, product, onSubmit }) {
  const [visible, setVisible] = useState(false);
  const [confirmLoading, setConfirmLoading] = useState(false);

  const showPopconfirm = () => {
    if (parcel.order?.items?.length < 2) {
      message.error('Removing single product is not allowed. Try cancelling or returning.');
      return;
    }
    setVisible(true);
  };

  const dispatch = useDispatch();
  const handleOk = useCallback(async () => {
    try {
      setConfirmLoading(true);
      const payload = {
        lineItemId: product.line_item_id,
        agentId: parcel.deliveryAgentId,
      };
      const {data} = await parcelAPI.cancelParcelProduct(
        parcel.invoiceNumber,
        payload,
      );

      dispatch(updateParcel({
        id: parcel.id,
        agentId: parcel.deliveryAgentId,
        data: { cash: data.parcel.cash }
      }))

      onSubmit(true);
    } catch (error) {
      message.error(error?.response?.data?.message || 'Something went wrong.')
    }
    setConfirmLoading(false);
    setVisible(false);
  }, [product, onSubmit]);

  const handleCancel = () => {
    setVisible(false);
  };

  return (
    <>
      <Popconfirm
        title="Are you sure to delete this product?"
        visible={visible}
        onConfirm={handleOk}
        okButtonProps={{ loading: confirmLoading }}
        onCancel={handleCancel}
        okText="Confirm"
      >
        <Button onClick={showPopconfirm} shape="circle" icon={<DeleteOutlined />} />
      </Popconfirm>
    </>
  );
}

