const B2bFilter = ({ filtersForm, onFilterChange, initialValues }) => {
  const prettifyValues = () => {
    const params = filtersForm.getFieldValue(); // contains limit, offset

    const data = { ...params };
    if (!data.trackingId) delete data.trackingId;
    if (!data.referenceNumber) delete data.referenceNumber;

    const [since, until] = data.dateRange || [];
    delete data.dateRange;

    onFilterChange({
      ...data,
      since,
      until,
    });
  };

  return (
    <Form
      layout="inline"
      form={filtersForm}
      initialValues={initialValues}
      onFinish={prettifyValues}
      css={filters_css}>
      <Form.Item name="trackingId">
        <Search
          placeholder="Tracking ID"
          style={{ width: 200 }}
          enterButton
          onSearch={filtersForm.submit}
          allowClear
          // onChange={filtersForm.submit}
        />
      </Form.Item>
      <Form.Item name="referenceNumber">
        <Search
          placeholder="Reference Number"
          style={{ width: 200 }}
          enterButton
          onSearch={filtersForm.submit}
          allowClear
          // onChange={filtersForm.submit}
        />
      </Form.Item>
      <Form.Item name="dateRange">
        <DateRangePicker onChange={filtersForm.submit} />
      </Form.Item>
      <Form.Item>
        <Button
          onClick={() => {
            filtersForm.resetFields();
            filtersForm.submit();
          }}>
          Reset
        </Button>
      </Form.Item>
    </Form>
  );
};

const BonusCommissionEditForm = () => {
  const router = useRouter();
  const dispatch = useDispatch();
  const { bonusCommissionData } = useSelector(
    (state: RootStateType) => state.salaryHrReducers.commissionReducer,
  );
  const { updateBonusCommissionLoading } = useSelector(
    (state: RootStateType) => state.salaryHrReducers.updateCommissionReducer,
  );

  useEffect(() => {
    fetchOperationalZones();
  }, []);

  const getBonusConfigValues = (
    kpiType: BonusCommissionKeyType,
    configType: keyof BonusConfigType,
  ) => {
    return bonusCommissionData[0].kpis[kpiType].config[configType]?.toString() || '';
  };

  const formatCustomHubIds = (hubIds: string) => {
    if (hubIds === '' || hubIds === allHubTypeIdentifier) {
      return [];
    }
    return convertHubsValueToList(hubIds);
  };

  useEffect(() => {
    if (bonusCommissionData.length > 0) {
      const bonusConfigValue = bonusCommissionData[0].kpis;
      formik.setValues({
        attendanceBonus: {
          enabled: bonusConfigValue.Attendance.isEnabled,
          minimumEligibleDays: getBonusConfigValues('Attendance', 'min_threshold'),
          maximumWorkingDays: getBonusConfigValues('Attendance', 'max_threshold'),
          minFridays: getBonusConfigValues('Attendance', 'friday_count'),
          bonusAmount: getBonusConfigValues('Attendance', 'incentive'),
          customHubs: formatCustomHubIds(getBonusConfigValues('Attendance', 'hub_id')),
        },
        fuelBonus: {
          enabled: bonusConfigValue.Fuel.isEnabled,
          minimumEligibleDays: getBonusConfigValues('Fuel', 'min_threshold'),
          maximumWorkingDays: getBonusConfigValues('Fuel', 'max_threshold'),
          bonusAmount: getBonusConfigValues('Fuel', 'incentive'),
          customHubs: formatCustomHubIds(getBonusConfigValues('Fuel', 'hub_id')),
        },
        linphoneBonus: {
          enabled: bonusConfigValue.LinphoneUsageKpi.isEnabled,
          percentage: getBonusConfigValues('LinphoneUsageKpi', 'min_threshold'),
          minimumCallDuration: getBonusConfigValues(
            'LinphoneUsageKpi',
            'linphone_call_duration',
          ),
          bonusAmount: getBonusConfigValues('LinphoneUsageKpi', 'incentive'),
          customHubs: formatCustomHubIds(
            getBonusConfigValues('LinphoneUsageKpi', 'hub_id'),
          ),
        },
        successRateBonus: {
          enabled: bonusConfigValue.SuccessRateKpi.isEnabled,
          percentage: getBonusConfigValues('SuccessRateKpi', 'min_threshold'),
          customHubs: formatCustomHubIds(
            getBonusConfigValues('SuccessRateKpi', 'hub_id'),
          ),
        },
        quantitativeBonus: {
          enabled: bonusConfigValue.QuantitativeRating.isEnabled,
          minimumPercentage: getBonusConfigValues('QuantitativeRating', 'min_threshold'),
          bonusAmount: getBonusConfigValues('QuantitativeRating', 'incentive'),
        },
        qualitativeBonus: {
          enabled: bonusConfigValue.QualitativeRating.isEnabled,
          minimumPercentage: getBonusConfigValues('QualitativeRating', 'min_threshold'),
          bonusAmount: getBonusConfigValues('QualitativeRating', 'incentive'),
        },
      });
    }
  }, [bonusCommissionData]);

