export const SelectHubTypeComponent = ({
  customHubs,
  setValue,
  bonusType,
}: SelectHubTypeComponentPropType) => {
  const [isCustomHubModal, openCustomHubsModal] = useState(false);

  const changeHubType = (value: string) => {
    if (value === 'customizeHub') {
      openCustomHubsModal(true);
    } else {
      setValue(`${bonusType}.customHubs`, []);
    }
  };
  const selectUnselectHubs = (hubs: number[]) => {
    setValue(`${bonusType}.customHubs`, hubs);
  };
  return (
    <>
      <Row gutter={[24, 1]} css={form_row_css}>
        <Col span={12}>
          <p>Hub selection</p>
          <Radio.Group
            data-testid="hub-selection-radio-group"
            onChange={(e) => changeHubType(e.target.value)}
            value={customHubs.length > 0 ? 'customizeHub' : 'allHub'}>
            <Radio
              value={'allHub'}
              onClick={() => changeHubType('allHub')}
              data-testid="all-hub-radio">
              All hub
            </Radio>
            <Radio
              value={'customizeHub'}
              onClick={() => changeHubType('customizeHub')}
              data-testid="custom-hub-radio">
              Customize hub
            </Radio>
          </Radio.Group>
        </Col>
        {customHubs.length > 0 && (
          <Col span={12}>
            <p>Hubs selected</p>
            <p>{customHubs.toString()}</p>
          </Col>
        )}
      </Row>
      <ZonesHubsSelection
        visible={isCustomHubModal}
        closeCustomizeHubsSelection={() => openCustomHubsModal(false)}
        customHubs={customHubs}
        selectUnselectHubs={selectUnselectHubs}
      />
    </>
  );
};

