const CloneCampaign = ({ cloneForm, campaignId }: CloneBasicCampaignPropType) => {
  const { setFieldValue, values, errors, touched } = cloneForm;
  const changeYear = (dateFormat: Moment | null, dateString: string) => {
    const previousMonth = moment(dateString).subtract(1, 'month');
    setFieldValue('cloneMonthValue', dateFormat);
    setFieldValue('cloneFromMonth', previousMonth.format('YYYY-MM'));
  };

  return (
    <>
      <p css={clone_label_css}>Campaign name</p>
      <Input
        placeholder="Enter campaign name"
        value={values.campaignName}
        onChange={(e) => setFieldValue('campaignName', e.target.value)}
      />
      <FormErrorText text={errors.campaignName} showError={touched.campaignName} />
      <p css={clone_label_css}>Clone from month</p>
      <DatePicker
        onChange={changeYear}
        picker="month"
        value={values.cloneMonthValue}
        allowClear={false}
      />
      <FormErrorText text={errors.cloneFromMonth} showError={touched.cloneFromMonth} />
      {isValidFieldValue(values.cloneFromMonth) && (
        <p
          css={
            warning_clone_css
          }>{`Campaign (ID - ${campaignId}) will be closed with ${values.cloneFromMonth}-26 as end date.`}</p>
      )}
    </>
  );
};

