const CycleDate = ({ handleChange, slotIndex, slot }: CycleDatePropType) => {
  const startDate = getStartDate();
  const endDate = getEndDate();

  const disabledDate = (date: Moment) => {
    const dayOfMonth = date.date();
    if (ignoredDatesForCycle.includes(dayOfMonth)) {
      return true;
    }
    if (date.isBefore(startDate) || date.isAfter(endDate)) {
      return true;
    }

    return false;
  };
  return (
    <DatePicker
      onChange={(value) => handleChange(`slots[${slotIndex}]`, moment(value))}
      disabledDate={disabledDate}
      disabled={slotIndex === 0}
      value={slot}
      allowClear={false}
      picker="date"
      format="D"
    />
  );
};

