const ZonesHubsSelection = ({
  visible,
  closeCustomizeHubsSelection,
  selectUnselectHubs,
  customHubs,
}: ZonesHubsSelectionPropType) => {
  const { operationalZones, operationalHubsForZones, hubsApiLoading } = useSelector(
    (state: RootStateType) => state.salaryHrReducers.salaryReducer,
  );
  const [selectedZoneId, setSelectedZoneId] = useState(-1);
  const dispatch = useDispatch();

  const selectZone = (value: number) => {
    setSelectedZoneId(value);
    dispatch(getHubsForZoneAsync({ operationalZoneId: value }));
  };

  const selectUnSelectParcels = (checked: boolean, selectedValue: number) => {
    if (checked) {
      selectUnselectHubs([...customHubs, selectedValue]);
    } else {
      const toRemoveValues = customHubs.filter((item) => item !== selectedValue);
      selectUnselectHubs(toRemoveValues);
    }
  };

  return (
    <div css={zone_css}>
      <Modal
        visible={visible}
        title={'Select zone and hubs'}
        footer={null}
        onCancel={closeCustomizeHubsSelection}>
        <div style={{ height: 300, overflow: 'scroll' }}>
          {operationalZones?.map((item: OperationalZoneType, zoneIndex: number) => {
            const isSelectedZone = zoneIndex + 1 === selectedZoneId;
            return (
              <div key={item.value}>
                <div onClick={() => selectZone(item.value)} css={zone_inner}>
                  <p>{item.label}</p>
                  <Button type="link">Select</Button>
                </div>
                <Spin spinning={isSelectedZone && hubsApiLoading} />
                {isSelectedZone &&
                  !hubsApiLoading &&
                  operationalHubsForZones?.map((item: OperationalZoneType) => {
                    return (
                      <div css={hubs_css} key={item.value}>
                        <Checkbox
                          checked={customHubs.includes(item.value)}
                          onChange={(e) =>
                            selectUnSelectParcels(e.target.checked, item.value)
                          }
                        />
                        <p style={{ marginLeft: 20 }}>{item.label}</p>
                      </div>
                    );
                  })}
              </div>
            );
          })}
        </div>
      </Modal>
    </div>
  );
};

