export function useFetchParcelsAndAgentsByInitialHubId({
  filtersForm,
  hubs,
  hubIdPassedInParam,
  trackingIdPassedInParam,
}: {
  filtersForm: FormInstance<any>;
  hubs: Array<{
    ID: number;
    IS_MH: number;
    HUB_NAME: string;
  }>;
  hubIdPassedInParam?: string;
  trackingIdPassedInParam?: string;
}) {
  const [agents, setAgents] = useState<AgentType[]>([]);

  const getAgentsByHubId = async (hubId: string, agentTypes: string) => {
    // agentTypes -> "type1,type2"
    try {
      const agent = await getAgent({ hubId, agentTypes });
      setAgents(agent);
    } catch (error) {
      message.error('Failed to get agents');
    }
  };

  useEffect(() => {
    if (hubs.length === 0) {
      return;
    }

    let hubIdToSet = `${hubs[0].ID}`;
    let hasAccessToGivenHubId = true;

    if (hubIdPassedInParam) {
      // we need to verify if user has access to this hubId
      // hubs coming from the API will have the hub if user has access
      // so, checking the hubId in the hubs props will suffice
      hasAccessToGivenHubId = hubs.some((hub) => hub.ID === parseInt(hubIdPassedInParam));
    }

    if (hasAccessToGivenHubId === false && hubIdPassedInParam !== undefined) {
      message.warning(
        "You don't have access to the given hub in the URL\n You can only see your own hub parcels",
        5,
      );
    }

    if (hasAccessToGivenHubId && hubIdPassedInParam) {
      hubIdToSet = hubIdPassedInParam;
    }

    filtersForm.setFields([{ name: 'ownHubId', value: hubIdToSet }]);

    if (trackingIdPassedInParam && hasAccessToGivenHubId) {
      filtersForm.setFields([{ name: 'trackingId', value: trackingIdPassedInParam }]);
    }

    filtersForm.submit();

    getAgentsByHubId(hubIdToSet, 'freelance_delivery');
  }, [hubs, filtersForm, hubIdPassedInParam, trackingIdPassedInParam]);

  return { agents, getAgentsByHubId, setAgents };
}

