export function useGetEscalationLevels() {
  const [escalationLevels, setEscalationLevels] = useState<EscalationLevelOptionType[]>(
    [],
  );

  const handleSetEscalationLevelOptions = (
    slaBreachTypeCodeName: SelectValue,
    slaEscalationLevelDefs: SlaEscalationLevelDefType[] = [],
    updateForm: (params: any) => void,
  ) => {
    if (!slaBreachTypeCodeName) {
      setEscalationLevels([]);
      updateForm({ [escalationLevelFieldName]: undefined });
      return;
    }

    const filterdEscalationLevelsWithSlaBreachKeys: EscalationLeveType[] = [];

    if (slaEscalationLevelDefs.length > 0) {
      slaEscalationLevelDefs.forEach((item) => {
        if (item.codeName === slaBreachTypeCodeName) {
          filterdEscalationLevelsWithSlaBreachKeys.push(
            ...item.escalationLevelsWithSlaBreachKeys,
          );
        }
      });
    }

    const optionsMapped = filterdEscalationLevelsWithSlaBreachKeys.map((option) => ({
      ...option,
      slaBreachKeys: option.slaBreachKeys.join(','),
    }));

    setEscalationLevels(optionsMapped || []);

    updateForm({ [escalationLevelFieldName]: optionsMapped?.[0]?.slaBreachKeys });
  };

  return { escalationLevels, handleSetEscalationLevelOptions };
}

