export const getDispatchStatusParam = (status?: string) => {
  if (!status) return;
  if (NON_LMH_STATUS_LABELS.includes(status)) {
    return false;
  }
  if (status === 'Dispatched to LMH') {
    return true;
  }
};

export const getDirectDispatchedParam = (status?: string) => {
  if (!status) return;
  if (DIRECT_DISPATCH_STATUS_LABELS.includes(status)) return true;
};

export const getSourceTypeParam = (pageType: string, status?: string) => {
  if (
    pageType === PAGE_TYPE.consignmentPanel ||
    status === 'Dispatched from sales booth'
  ) {
    return 'consignment-panel';
  }
  if (!status) return;
  if (
    status === 'Received at MH' ||
    (status === 'Created at mother hub' && pageType === PAGE_TYPE.orderPanel)
  ) {
    return 'mother-hub-panel';
  }
};

export const getC2cStatusParam = (pageType: string, status?: string) => {
  if (pageType === PAGE_TYPE.consignmentPanel) return PICKUP_PENDING_STATUS;
  if (!status) return;
  if (pageType === PAGE_TYPE.orderPanel) {
    return C2C_STATUS_MAP[status];
  }
  return status;
};

export const getC2cStatusOptions = () => {
  return Object.keys(C2C_STATUS_MAP).map((status) => ({
    id: status,
    name: status,
  }));
};

export const getOrderPanelStatusText = (record: ConsignmentType) => {
  if (record.status === 'pickup-pending' && record.sourceType === 'consignment-panel') {
    return C2C_STATUS_REVERSE_MAP['pickup-pending-sales-booth'];
  }
  if (record.status === 'delivery-in-progress' && !record.isDirectDispatch) {
    return C2C_STATUS_REVERSE_MAP['hub-transfer'];
  }
  if (record.status === 'hub-transfer' && !record.dispatchedOnDate) {
    if (record.sourceType === 'consignment-panel') {
      return C2C_STATUS_REVERSE_MAP['hub-transfer-consignment-panel'];
    }
    return C2C_STATUS_REVERSE_MAP['hub-transfer-order-panel'];
  }
  return C2C_STATUS_REVERSE_MAP[record.status];
};

