export const formatPayloadForBulkUpdateConsignmentParcels = (
  consignmentParcels: LogisticsParcelsType[],
  parcelModulestatusActionMap: StatusActionMapType,
  ownHubId: number,
) => {
  if (!parcelModulestatusActionMap) return;
  const [oldStatus] = Object.keys(parcelModulestatusActionMap);
  const [action] = Object.keys(parcelModulestatusActionMap[oldStatus]);
  const currentStatus = parcelModulestatusActionMap[oldStatus][action].nextStatus;
  const parcels: C2CBulkUpdateParcelType[] = consignmentParcels.map((parcel) => {
    return {
      id: parcel.trackingId,
      sourceHubId: ownHubId,
      destinationHubId: parcel.nextHubId,
      currentStatus,
      oldStatus: parcel.status,
    };
  });
  return {
    action,
    parcels,
  };
};

