export function formatAddAdjustmentPayload(
  formValues: AdjustBillFormType,
  cpId: number,
  debitNoteUrl: string,
) {
  const isBillAdjustment = formValues.adjustmentType === 'value';
  const orderAdjustments: Record<string, number> = {};

  formValues.adjustmentValues.forEach((adjustment) => {
    orderAdjustments[adjustment.cnNumber] = adjustment.value;
  });

  return {
    cp_id: cpId,
    adjustment_against: !isBillAdjustment ? 'order' : 'bill',
    bill_adjustment_amount: isBillAdjustment ? formValues.adjustmentAmount : 0,
    debit_note_file_url: debitNoteUrl,
    order_adjustments: orderAdjustments,
  } as AddAdjustmentPayloadType;
}

