export function validateTransactionId(form: FormInstance): RuleObject {
  const paymentMethod = form.getFieldValue('paymentMethod');
  if (paymentMethod !== 'Cash')
    return {
      required: true,
      whitespace: true,
      pattern: new RegExp(/^[a-zA-Z\d]+$/i),
      message: '*Valid ID required',
    };
  return {
    pattern: new RegExp(/^[a-zA-Z\d]+$/i),
    message: '*Valid ID required',
  };
}

