export function formatCsvData(data: InvoiceExcelData[]) {
  const rowData: string[][] = [PREPARED_BILL_CSV_COLUMNS];

  let totalAmount = 0;
  data.forEach((invoice) => {
    const currentInvoiceData: string[] = [];
    currentInvoiceData.push(invoice.invoiceNumber);
    currentInvoiceData.push(invoice.cn_id);
    currentInvoiceData.push(invoice.shop_name);
    currentInvoiceData.push(invoice.receivable_amount.toString());
    currentInvoiceData.push(invoice.adjustment_amount.toString());
    currentInvoiceData.push(invoice.bill_amount.toString());
    currentInvoiceData.push(invoice.receiver_name);
    currentInvoiceData.push(invoice.delivery_area);
    currentInvoiceData.push(invoice.no_of_parcels.toString());
    currentInvoiceData.push(invoice.created_at);

    totalAmount += invoice.bill_amount;

    rowData.push(currentInvoiceData);
  });

  rowData.push([]);
  rowData.push(['Total Bill Amount', totalAmount.toString()]);

  return rowData;
}

