export function downloadFilteredIssues(
  formValues: any,
  moduleCode: string,
  handleIssueDownloadLoader: (value: boolean) => void,
) {
  const { parcelId, shopId, operationalZoneId, hubId } = formValues;

  const typeId = formValues.typeId
    ? lodash.last(formValues.typeId.split('-'))
    : undefined;

  const slaKeys = formValues[escalationLevelFieldName]
    ? formValues[escalationLevelFieldName].split(',')
    : undefined;

  let [since, until] = formValues.dateRange || [
    moment().subtract(30, 'days').startOf('day').format('x'),
    moment().endOf('day').format('x'),
  ];
  since = moment.isMoment(since) ? moment(since).startOf('day').format('x') : since;
  until = moment.isMoment(until) ? moment(until).endOf('day').format('x') : until;

  issueV3API
    .downloadIssues({
      moduleCode,
      parcelId,
      shopId,
      typeId,
      slaKeys,
      operationalZoneId,
      hubId,
      since,
      until,
    })
    .then((response) => {
      downloadfromCSVContent(response, Date.now());
      handleIssueDownloadLoader(false);
    })
    .catch((error: any) => {
      message.error(error.message || 'Something went wrong with the server');
      handleIssueDownloadLoader(false);
    });
}

export function prepareCommonParametersForFormSubmission(funcArguments: ArgumentsType) {
  const {
    formValues,
    initialParameters,
    tableStateParams,
    slaEscalationLevelDefs,
    slaFilterRole,
    entityType,
  } = funcArguments;

  let shopId;
  let since;
  let until;

  if (formValues.shopId) {
    shopId = formValues.shopId.value;
  }

  if (formValues.dateRange) {
    const [startDate, endDate] = formValues.dateRange;
    since = moment(startDate).startOf('day').format('x');
    until = moment(endDate).endOf('day').format('x');

    delete formValues.dateRange;
  }

  // set default sla keys
  const slaKeys = formValues[escalationLevelFieldName]
    ? formValues[escalationLevelFieldName].split(',')
    : getDefaultSlaKeys({ slaEscalationLevelDefs, slaFilterRole });

  delete formValues[escalationLevelFieldName];
  delete formValues[slaBreachTypeFieldName];

  const typeId = formValues.typeId ? last(formValues.typeId.split('-')) : undefined;

  return {
    ...tableStateParams,
    ...formValues,
    shopId,
    typeId,
    slaKeys,
    limit: initialParameters.limit,
    offset: initialParameters.offset,
    since,
    until,
    entityType,
  };
}

export function prepareCommonParametersForTableFilters(
  filters: Record<string, FilterValue | null>,
  tableStateParams: ParametersType,
  initialParameters: InitialParametersType,
) {
  // filtered value always come as an array
  // currently no way to change that, unless we render our own filter menu
  const severityId = filters.severityId ? (filters.severityId[0] as number) : undefined;

  // when resetting filter, value becomes null
  // null does not auto remove from API param when qs.stringify
  // we can write a serlizer function to remove null values but
  // that function complexity will be O(n^2)
  // so, making filter value undefind with ternary operator
  // making it O(1)

  const status = filters.status ? (filters.status as string[]) : undefined;
  const bulkIssueStatus = filters.bulkIssueStatus
    ? (filters.bulkIssueStatus as string[])
    : undefined;

  return {
    ...tableStateParams,
    ...filters,
    severityId,
    status,
    bulkIssueStatus,
    limit: initialParameters.limit,
    offset: initialParameters.offset,
  };
}

export async function downloadLinehaulBulkPdfAssignedToAgent({
  agentId,
  type,
  types,
  startHubId,
  status = 'line-haul-trip-started',
  statuses,
  tripId,
}) {
  let bulkGetRes;
  try {
    bulkGetRes = await lineHaulApi.getBulks({
      tripId,
      status,
      startHubId,
      type,
      types,
      statuses,
      transferAgentId: agentId,
    });
  } catch (bulkGetError) {
    return bulkGetError;
  }

  const parentBulks = bulkGetRes.rows.filter(
    (row) => row.secondaryBulkTransferId !== null,
  );

  if (parentBulks.length === 0) {
    alert('No trip to download');
    return;
  }

  const groupParentBulks = lodash.groupBy(parentBulks, (item) => {
    const secondaryBulkIds = item.secondaryBulkTransferId.split(',');
    return `${item.endHub.id}_${secondaryBulkIds[0]}`;
  });

  console.log('groupParentBulks', groupParentBulks);

  generatePdfForLinehaulBulkAgentAssignment(groupParentBulks);
}

export function generatePdfForLinehaulBulkAgentAssignment(groupParentBulks) {
  const groupParentBulksKeys = Object.keys(groupParentBulks);

  const contentTemplate = [];

  for (let i = 0; i < groupParentBulksKeys.length; i++) {
    const isPageBreak = groupParentBulksKeys.length - 1 !== i;

    contentTemplate.push(
      getHubWiseContentTemplate(groupParentBulks[groupParentBulksKeys[i]], isPageBreak),
    );
  }

  const template = {
    content: [...contentTemplate],
    images: {
      redx_logo: `https://${url}/images/redx_logo.jpg`,
    },
    ...getDefaultTemplateConfigs(),
  };

  generateAndOpenPdf(template);
}

