export const getLineHaulTypes = (
  enableDecentralisation: boolean,
  parcelTypes?: string[],
) => {
  const targetLineHaulTypes: string[] = [];
  if (Array.isArray(parcelTypes)) {
    if (parcelTypes.includes(KEY_C2C)) {
      targetLineHaulTypes.push(lineHaulTypes.c2c);
    }
    if (parcelTypes.includes(KEY_3PL)) {
      if (enableDecentralisation) {
        targetLineHaulTypes.push(...DECENTRALIZED_LINEHAUL_TYPES);
      } else {
        targetLineHaulTypes.push(...DEFAULT_LINEHAUL_TYPES);
      }
    }
    return targetLineHaulTypes;
  }
};

