export const getOptionsFromVehicleCapacities = (
  vehicleCapacities: VehicleCapacityConfigMapType,
) => {
  const feetOptions = Object.keys(vehicleCapacities);
  const typeOptionsArray: Record<string, string[]> = {};
  const typeOptions: Record<string, AntdOptionType[]> = {};
  const tonOptionsArray: Record<string, number[]> = {};
  const tonOptions: Record<string, AntdOptionType[]> = {};
  feetOptions.forEach((ft) => {
    vehicleCapacities[ft].forEach((capacity) => {
      const { type, ton } = capacity;
      if (Array.isArray(typeOptions[ft]) && !typeOptionsArray[ft].includes(type)) {
        typeOptions[ft].push(convertValueToOption(type));
        typeOptionsArray[ft].push(type);
      }
      if (!Array.isArray(typeOptions[ft])) {
        typeOptions[ft] = [convertValueToOption(type)];
        typeOptionsArray[ft] = [type];
      }
      if (
        Array.isArray(tonOptions[`${ft}-${type}`]) &&
        !tonOptionsArray[`${ft}-${type}`].includes(ton)
      ) {
        tonOptions[`${ft}-${type}`].push(
          convertValueToOption(ton, decorateCapacityTonValue),
        );
        tonOptionsArray[`${ft}-${type}`].push(ton);
      }
      if (!Array.isArray(tonOptions[`${ft}-${type}`])) {
        tonOptions[`${ft}-${type}`] = [
          convertValueToOption(ton, decorateCapacityTonValue),
        ];
        tonOptionsArray[`${ft}-${type}`] = [ton];
      }
    });
  });
  return {
    feetOptions: convertArrayToOptionArray(feetOptions, decorateCapacityFeetValue),
    typeOptions,
    tonOptions,
  };
};

