export const isStartTripBtnDisabled = ({
  enableCargoCorporateIntegrationForLinehaul,
  truckLicenseNumber,
  mode,
  tripMode,
  selectedTripId,
  linehaulRollOutStatus,
}: {
  enableCargoCorporateIntegrationForLinehaul: boolean;
  truckLicenseNumber: string;
  tripMode?: TripModeType;
  mode: string;
  selectedTripId?: number;
  linehaulRollOutStatus: string;
}) => {
  if (linehaulRollOutStatus !== 'fulfilled') {
    return true;
  }
  if (!enableCargoCorporateIntegrationForLinehaul) {
    return truckLicenseNumber.length === 0;
  }
  return (
    !tripMode ||
    (mode === NEW_TRIP && truckLicenseNumber.length === 0) ||
    (mode === EXISTING_TRIP && !selectedTripId)
  );
};

export const getFleetRouteMap = (
  fleets: FleetInventoryType[],
): Record<number, FleetInventoryType[]> => {
  return fleets.reduce((acc: Record<number, FleetInventoryType[]>, fleet) => {
    if (fleet.extraData?.routeId) {
      if (!acc[fleet.extraData.routeId]) {
        acc[fleet.extraData.routeId] = [];
      }
      acc[fleet.extraData.routeId].push(fleet);
    }
    return acc;
  }, {});
};

export const getHubNameFromRouteHubs = (value: RouteHubType[]) => {
