};

export const validateTimeBetweenHubs = (rule: any, value: string) => {
  if (value === undefined || value === '') {
    return Promise.resolve();
  }

  if (!/^\d+$/.test(value)) {
    return Promise.reject('Please enter an integer');
  }

  const intValue = parseInt(value, 10);
  if (intValue < 0 || intValue > 24) {
    return Promise.reject('Average time should be in range 0-24');
  }

  return Promise.resolve();
};

export const getAvailablevehicles = ({
  routeId,
  fleetInventoryList,
}: {
  routeId: number | null;
  fleetInventoryList: FleetInventoryType[];
}) => {
  const availableVehicleList: FleetInventoryType[] = [];

  for (const fleet of fleetInventoryList) {
    if (routeId && fleet.extraData?.routeId === routeId) {
      availableVehicleList.push(fleet);
    }

    if (!fleet.extraData?.routeId) {
      availableVehicleList.push(fleet);
    }
  }

  return availableVehicleList;
};

