export default function DispatchISD() {
  const dispatch = useDispatch();
  const {
    selections: { unscanned, scanned, scannedIds },
    dispatch: scannerDispatch,
  } = useScanner({
    logger: true,
    initialState: {
      acceptDuplicateItems: false,
      redisKey: `cashReceiveConfirmedConsignments`,
      scanMatchProperty: 'id',
      isGlobal: false,
    },
    selectors: {
      unscanned: selectUnscanned,
      scanned: selectScanned,
      scannedIds: selectScannedIds,
    },
  });
  const initialParams = {
    status: 'delivered',
    isDirectDispatch: true,
    startDate: undefined,
    endDate: undefined,
    bookingType: undefined,
    invoiceNumber: undefined,
  };
  const [consignmentsFetchParams, setConsignmentFetchParams] = useState(initialParams);
  const _persist = useSelector((state) => state._persist);
  const {
    consignments,
    getConsignmentsRequestStatus,
    markConsignmentAsCashReceivedStatus,
  } = useSelector((state) => state.c2cDeliveredConsignmentSlice);
  const { hubs } = useSelector((state) => state.hub);
  const [isReturning, setReturning] = useState(false);
  const [isCashReceiving, setCashReceiving] = useState(false);
  const [isSyncDone, setSyncDone] = useState(false);
  const [isConsignmentFetchDone, setConsignmentFetchDone] = useState(false);

  function showCashReceiveConfirm() {
    Modal.confirm({
      title: `Are you sure to mark the confirmed consignments as Cash Received?`,
      icon: <ExclamationCircleOutlined />,
      okText: 'Confirm',
      closable: true,
      onOk() {
        _markConsignmentAsCashReceived();
      },
      onCancel() {
        console.log('Cancel');
      },
    });
  }

  const syncItemsInRedis = async (items, scanMatchProperty) => {
    const itemIds = items.map((item) => item[scanMatchProperty].toString());
    const idsToBeFiltered = scannedIds.filter(
      (scannedId) => !itemIds.includes(scannedId),
    );
    if (idsToBeFiltered.length > 0) {
      await scannerDispatch(removeItemsAsync(idsToBeFiltered));
    }
  };

  const fetchConsignments = async () => {
    const { payload: res } = await dispatch(
      getConsignmentsAsync(consignmentsFetchParams),
    );
    if (res?.isError === false) {
      await scannerDispatch(loadItems(res.data));
      setConsignmentFetchDone(true);
    } else {
      message.error(res?.message || 'Something went wrong');
    }
  };

  const removeCashReceivedOrReturnedConsignments = async (consignmentIds) => {
    await scannerDispatch(removeItemsAsync(consignmentIds));
  };

