const ConsignmentRaiseFlag = () => {
  const router = useRouter();
  const { consignmentId, consignmentTrackingId } = router.query;
  const dispatch = useDispatch();
  const {
    getConsignmentFlagQuestionsRequestStatus,
    raiseFlagRequestStatus,
    consignmentFlagQuestions,
  } = useSelector((state) => state.consignmentV2);
  const [consignmentQuestions, setConsignmentQuestions] = useState();
  const [flagRaised, setFlagRaised] = useState(false);

  const [form] = Form.useForm();

  const getStringifiedAnswers = (consignmentQuestions, quesKey) => {
    const answers = consignmentQuestions[quesKey].answers
      .filter((a) => a.answer)
      .map((a) => a.answer);
    if (answers.length === 0) {
      return 'N/A';
    }
    return JSON.stringify(answers.join(' '));
  };

  useEffect(() => {
    dispatch(getFlagQuestionsAsync({ consignmentId }));
  }, []);

  useEffect(() => {
    if (consignmentFlagQuestions) {
      const { qa, isFlagRaised } = consignmentFlagQuestions;
      let groupedQuestions = lodash.keyBy(qa, 'questionKey');
      setConsignmentQuestions(groupedQuestions);
      setFlagRaised(isFlagRaised);
    }
  }, [consignmentFlagQuestions]);

  useEffect(() => {
    if (raiseFlagRequestStatus === 'fulfilled') {
      message.info('Flag Raised for the consignment successfully!');
      router.back();
      dispatch(setRaiseFlagRequestStatus());
    }
  }, [raiseFlagRequestStatus]);

  const onSubmit = async () => {
    const values = form.getFieldValue();
    const payload = Object.keys(consignmentQuestions).map((quesKey) => {
      return {
        questionKey: quesKey,
        answer: values[quesKey],
      };
    });
    if (payload.every((p) => !p.answer)) {
      message.error('Please answer any one of the questions to submit the form!');
      return;
    }
    dispatch(
      raiseFlagAsync({
        consignmentId,
        qa: payload,
      }),
    );
  };

  return (
    <ShopUpLayout>
      <PageHeader
        className="pageHeader"
        title={
          <Space align="center">
            <Button
              shape="circle"
              icon={<LeftOutlined />}
              onClick={() => router.back()}
            />
            {`Raise flag for ${consignmentTrackingId} `}
          </Space>
        }
      />

