export default function CouponList() {
  const router = useRouter();
  const [_couponState, _couponDispatch] = useReducer(couponReducer, couponInitialState);
  const [_modalStates, _disptachModalAction] = useReducer(modalReducer, modalIntialState);
  const { expireCouponStatus } = useSelector((state: any) => state.coupon);
  const dispatch = useDispatch();

  const [tableFilters, setTableFilters] = useState({
    code: undefined,
    type: undefined,
    creationDateRangeStart: undefined,
    creationDateRangeEnd: undefined,
  });

  const handleShopSearch = async (value: string) => {
    _disptachModalAction({ type: 'SHOP_SEARCH_PENDING' });

    try {
      const res = await ShopService.searchShopByShopId(value);
      _disptachModalAction({ type: 'UPDATE_SEARCHED_SHOPS', payload: res });
    } catch (e: any) {
      message.error(e.message || 'Shop search error');
    }
    _disptachModalAction({ type: 'SHOP_SEARCH_COMPLETED' });
  };

  const handleShopSelect = (value: number) => {
    const _foundShop = _modalStates.searchedShops.find(
      ({ id }: { id: number }) => id === value,
    );
    _disptachModalAction({ type: 'UPDATE_SELECTED_SHOP', payload: _foundShop });
  };

  const _applyCoupon = async (code: number, shopId: number) => {
    _disptachModalAction({ type: 'CODE_APPLY_TO_SHOP_PENDING' });
    try {
      await applyCoupon(code, [shopId]);
      message.success('Coupon code applied successfully');
    } catch (e: any) {
      message.error(e.message);
    }
    _disptachModalAction({ type: 'CLOSE_MODAL' });
  };

  const fetchCouponList = useCallback(async (_params) => {
    _couponDispatch({ type: 'DATA_FETCHING_PENDING' });
    try {
      const { offers, totalCount } = await getCouponOffers(_params);
      _couponDispatch({
        type: 'UPDATE_COUPON_LIST',
        payload: { offers, totalCount },
      });
    } catch (e: any) {
      message.error(e.message || 'Something went wrong');
    }
    _couponDispatch({ type: 'DATA_FETCHING_COMPLETE' });
  }, []);

  useEffect(() => {
    fetchCouponList({ ..._couponState.params });
  }, [_couponState.params, fetchCouponList]);

  const handleCouponCodeChange = (e: any) => {
    const value = e.target.value;
    setTableFilters({
      ...tableFilters,
      code: value || undefined,
    });
  };

  const handleDateRangeChange = (dates: any) => {
    console.log(dates);
    const [startDate, endDate] = dates || [];
    setTableFilters({
      ...tableFilters,
      creationDateRangeStart: startDate ? startDate.toISOString() : undefined,
      creationDateRangeEnd: endDate ? endDate.endOf('day').toISOString() : undefined,
    });
  };

