  const [zones, setZones] = useState([]);
  const [mokamHubs, setMokamHubs] = useState([]);
  const [otpParcels, setOtpParcels] = useState([]);

  const [filterValues, setFilterValues] = useState({});

  const queryObject = useMemo(() => ({ ...defaultQueryObject, ...filterValues }), [
    filterValues,
  ]);

  // check mokam de role to load mokam parcels
  const { user } = useSelector((state) => state.auth);
  const userRoles = user !== null ? user.Roles.map((role) => role.name) : null;
  const { modeState } = useSelector((state) => {
    return {
      modeState: state.dePanelModeSlice,
    };
  });

  const { data: otpParcelsData, isFetching, isRefreshing } = useDeParcelsOtp({
    mode: modeState.currentMode,
  });

  const dispatch = useDispatch();
  useEffect(() => {
    if (
      modeState.allowedModes.length > 0 &&
      (otpParcelsData.length || otpParcels.length)
    ) {
      setOtpParcels(otpParcelsData);
    }
  }, [modeState, otpParcelsData]);

  const hanldeCurrentModeChange = (mode) => {
    dispatch(setCurrentMode(mode.target.value));
  };
  const filteredData = useMemo(() => {
    const {
      agentPhoneNumber = '',
      parcelTrackingId = '',
      invoiceNumber = '',
      zoneId,
      parcelValue,
      sourceHubId,
      consignmentId,
    } = queryObject;
    const phoneRegex = new RegExp(`^.*${agentPhoneNumber.split('').join('.*')}.*$`, 'i');
    const trackingRegex = new RegExp(
      `^.*${parcelTrackingId.split('').join('.*')}.*$`,
      'i',
    );
    const invoiceNumberRegex = new RegExp(
      `^.*${invoiceNumber.split('').join('.*')}.*$`,
      'i',
    );

    return otpParcels.filter(
      ({
        DELIVERY_AGENT_PHONE,
        TRACKING_ID,
        OP_ZONE_ID,
        ZONE_ID,
        CASH,
        INVOICE_NUMBER,
        SOURCE_HUB_ID,
        CONSIGNMENT_ID,
      }) =>
        phoneRegex.test(DELIVERY_AGENT_PHONE) &&
        trackingRegex.test(TRACKING_ID) &&
        invoiceNumberRegex.test(INVOICE_NUMBER) &&
        (zoneId ? (OP_ZONE_ID || ZONE_ID) === +zoneId : true) &&
        (sourceHubId ? SOURCE_HUB_ID === +sourceHubId : true) &&
        (parcelValue ? CASH === +parcelValue : true) &&
        (consignmentId ? CONSIGNMENT_ID === +consignmentId : true),
    );
  }, [otpParcels, queryObject]);

  const columns = useMemo(() => getColumns({ mode: modeState.currentMode }), [modeState]);

