      if (queryObject.shopId) {
        dispatch(fetchPostpaidInvoices(queryObject));
      }
    },
    [dispatch],
  );

  const columns = useMemo(() => getColumns(), []);

  useEffect(() => {
    if (isHydrating) {
      return;
    }
    fetchData(queryObject);
  }, [queryObject, fetchData, isHydrating]);

  const data = useMemo(
    () => (postpaidInvoices.allIds || []).map((id) => postpaidInvoices.byId[id]),
    [postpaidInvoices],
  );

  const onFilterChange = useCallback((params) => {
