const TransferReference = () => {
  const [pdfFile, setPdfFile] = useState<UploadFile>();
  const [excelFile, setExcelFile] = useState<UploadFile>();
  const [confirmationLoading, setConfirmationLoading] = useState(false);

  const confirmUpload = async () => {
    if (!(pdfFile && excelFile)) {
      message.error('Upload respective files to confirm');
      return;
    }
    setConfirmationLoading(true);
    const pdfFormData = new FormData();
    const excelFormData = new FormData();
    pdfFormData.append('margin-transfer-reference', pdfFile.originFileObj);
    try {
      const pdfUploadedResponse = await finance.uploadTransferReferencePdf(pdfFormData);
      if (pdfUploadedResponse && !pdfUploadedResponse.isError) {
        try {
          excelFormData.append(
            'combined-invoice-margin-transfer-reference-number',
            excelFile.originFileObj,
          );
          excelFormData.append(
            'attachmentReferenceId',
            pdfUploadedResponse.uniqueIdentifier,
          );
          const excelUploadedResponse = await finance.uploadMarginTransferExcel(
            excelFormData,
          );
          if (excelUploadedResponse && !excelUploadedResponse.isError) {
            message.success('Files uploaded successfully');
            setExcelFile(undefined);
            setPdfFile(undefined);
          } else {
            message.error(excelUploadedResponse.message || excelUploadFailedError);
          }
        } catch (err: any) {
          message.error(err.message || err?.error?.message || excelUploadFailedError);
        }
      } else {
        message.error(pdfUploadedResponse.message || pdfUploadFailedError);
      }
    } catch (err: any) {
      console.log('error', err);
      message.error(err.message || err?.error?.message || pdfUploadFailedError);
    }
    setConfirmationLoading(false);
  };

  return (
    <ShopUpLayout hideFooter>
      <PageHeader
        heading="Transfer reference"
        subHeading="Upload right document and invoice"
      />
      <UploadReferenceTemplate
        title={`Internal bank transfer ref.`}
        sampleDownloadLink={googleDocLinks.combinedInvoiceMarginTransfer}
        sampleAliasName={`Sample_Bulk_RedX_Invoice`}
        confirmUpload={confirmUpload}
        disableConfirm={confirmationLoading}
        pdfFile={pdfFile}
        setPdfFile={setPdfFile}
        excelFile={excelFile}
        setExcelFile={setExcelFile}
        pdfLabel={'Upload PDF'}
        excelLabel={'Upload reference number'}
      />
    </ShopUpLayout>
  );
};

