function FailedPayments() {
  const [_state, _dispatch] = useReducer(failedPaymentReducer, initialState);

  const _getBouncedPayments = useCallback(async (params = _state.params) => {
    _dispatch({ type: DATA_FETCHING_PENDING });
    try {
      const response = await FinanceSrv.getBouncePayment(params);
      _dispatch({
        type: UPDATE_TABLE_DATA,
        payload: { totalData: response.data.count, tableData: response.data.items },
      });
    } catch (error) {
      message.error(error.message || 'Failed to fetch bounce payments');
    }
    _dispatch({ type: DATA_FETCHING_COMPLETE });
  }, []);

  const _getAllKam = useCallback(async () => {
    try {
      const response = await FinanceSrv.getAllKam();

      _dispatch({
        type: UPDATE_KAM_DATA,
        payload: response.data.items || [],
      });
    } catch (error) {
      message.error(error.message || 'Failed to fetch kam data');
    }
  }, []);

  const _resolveBouncePayment = async (_bouncedAccountId) => {
    try {
      await FinanceSrv.resolveBouncePayment(_bouncedAccountId);
      await _getBouncedPayments(_state.params);
      message.success('Payment resolve successfull');
    } catch (error) {
      message.error(
        error.message || 'Unable to resolve bounce payment due to server error',
      );
    }
  };

  const updateParams = (_params) => {
    _dispatch({
      type: UPDATE_PARAMS,
      payload: {
        ..._state.params,
        ..._params,
      },
    });
  };

  const handleDateRangeChange = (dates) => {
    const [startDate, endDate] = dates || [];

    updateParams({
      dateFrom: startDate ? startDate.toISOString() : undefined,
      dateTo: endDate ? endDate.endOf('day').toISOString() : undefined,
    });
  };

  const onPageNumberChange = (page, pageSize) => {
    const _offset = page * pageSize - pageSize;
    _getBouncedPayments({ ..._state.params, offset: _offset });
  };

  useEffect(() => {
    _getAllKam();
    _getBouncedPayments();
  }, [_getBouncedPayments, _getAllKam]);

  return (
