export const useGenericScanner = <T extends CustomObjectType>(
  props: ScannerSlicePropsType<T>,
) => {
  const { initialState } = props;

  sounds = useSounds();

  const { reducer, actions } = useMemo(() => createScannerSlice(initialState), [
    initialState,
  ]);

  const [state, dispatch] = useReducer(reducer, initialState);

  const thunkSupportedDispatch = useCallback(
    async (action) => {
      if (typeof action === 'function') {
        await action(thunkSupportedDispatch, () => state);
      } else {
        dispatch(action);
      }
    },
    [state],
  );

  const { items, scannedIds, identifier } = state;

  const selections = useMemo(() => {
    const scanned: T[] = [];
    const unscanned: T[] = [];
    const scannedIdsFromItems: string[] = [];
    items.forEach((item) => {
      if (scannedIds.includes(item[identifier] as string)) {
        scanned.push(item);
        scannedIdsFromItems.push(item[identifier]);
      } else {
        unscanned.push(item);
      }
    });
    return { scanned, unscanned, scannedIdsFromItems };
  }, [items, identifier, scannedIds]);

  return { state, selections, dispatch: thunkSupportedDispatch, actions };
};

