export const scanItemsByIds = async <T extends CustomObjectType>(
  ids: string[],
  thunkAPI: ThunkApiType,
) => {
  const state = thunkAPI.getState() as ScannerSliceStateType<T>;
  const {
    items,
    identifier,
    scannedIds,
    redisKey,
    isGlobalRedis,
    scanProperties,
  } = state;
  const updatedScannedIds = [...scannedIds];
  ids.forEach((targetId) => {
    const targetItem = queryTargetItem(items, targetId, identifier, scanProperties);
    if (!targetItem) {
      throw new Error('Invalid ID!');
    }
    if (scannedIds.includes(targetItem[identifier])) {
      throw new Error('Already scanned!');
    }
    updatedScannedIds.push(targetItem[identifier]);
  });
  await redisAPI.setItemInRedis({
    isGlobal: isGlobalRedis,
    key: redisKey,
    value: JSON.stringify(updatedScannedIds),
  });
  return { scannedIds: updatedScannedIds };
};

export const fetchScannedIds = async <T extends CustomObjectType>(
  _: void,
  thunkAPI: ThunkApiType,
) => {
  const state = thunkAPI.getState() as ScannerSliceStateType<T>;
  const { redisKey, isGlobalRedis } = state;
  let scannedIdsJSON;
  try {
    scannedIdsJSON = await redisAPI.getItemFromRedis({
      isGlobal: isGlobalRedis,
      key: redisKey,
    });
  } catch (e) {
    return { scannedIds: [] };
  }
  if (!scannedIdsJSON) {
    return { scannedIds: [] };
  }
  const scannedIds = JSON.parse(scannedIdsJSON.toString()) as string[];
  return { scannedIds: scannedIds };
};

export const unscanItemsByIds = async <T extends CustomObjectType>(
  ids: string[],
  thunkAPI: ThunkApiType,
) => {
  const state = thunkAPI.getState() as ScannerSliceStateType<T>;
  const {
    items,
    identifier,
    scannedIds,
    redisKey,
    isGlobalRedis,
    scanProperties,
  } = state;
  let updatedScannedIds = [...scannedIds];
  ids.forEach((targetId) => {
    const targetItem = queryTargetItem(items, targetId, identifier, scanProperties);
    if (!targetItem) {
      throw new Error('Invalid ID!');
    }
    if (!scannedIds.includes(targetItem[identifier])) {
      throw new Error('Item not scanned yet!');
    }
    updatedScannedIds = updatedScannedIds.filter((id) => id !== targetItem[identifier]);
  });
  await redisAPI.setItemInRedis({
    isGlobal: isGlobalRedis,
    key: redisKey,
    value: JSON.stringify(updatedScannedIds),
  });
  return { scannedIds: updatedScannedIds };
};

export const removeItemsByIds = async <T extends CustomObjectType>(
  ids: string[],
  thunkAPI: ThunkApiType,
) => {
  const state = thunkAPI.getState() as ScannerSliceStateType<T>;
  const { items, identifier, scannedIds, redisKey, isGlobalRedis } = state;
  let updatedScannedIds = [...scannedIds];
  let updatedItems = [...items];
  ids.forEach((targetId) => {
    const targetItem = queryTargetItem(items, targetId, identifier);
    if (targetItem) {
      updatedScannedIds = updatedScannedIds.filter((id) => id !== targetItem[identifier]);
      updatedItems = updatedItems.filter(
        (item) => item[identifier] !== targetItem[identifier],
      );
    }
  });
  await redisAPI.setItemInRedis({
    isGlobal: isGlobalRedis,
    key: redisKey,
    value: JSON.stringify(updatedScannedIds),
  });
  return { scannedIds: updatedScannedIds, items: updatedItems };
};

