export const uploadFile = async (payload: UploadFilePayloadType) => {
  const { invoiceId, labelPrefix, entityType, file } = payload;
  const identifier = formatDateTime(new Date());

  const formData = new FormData();
  formData.append('image', file);
  formData.append('entityLabel', `${labelPrefix}_${identifier}`);

  const { data: response } = await axiosWithTypes.post<SlImageUploadResponseType>(
    `/v2/logistics/image-upload/${entityType}/ltl-credit-billing/${invoiceId}`,
    formData,
  );

  if (response.isError) {
    throw new Error(response.message);
  }
  return response;
};

