function CourseModal({ hubId, course, areas, onSuccess = () => null }) {
  const [isModalVisible, setIsModalVisible] = useState(false);
  areas = areas.concat(course.courseArea);

  const onOpen = () => setIsModalVisible(true);
  const onClose = () => setIsModalVisible(false);

  const dispatch = useDispatch();
  const handleSubmit = useCallback(
    async (formValues) => {
      try {
        const courseId = course.ID;
        const courseName = formValues.COURSE_NAME;
        const areaIds = formValues.courseArea;
        await dispatch(updateCourseAsync({ courseId, courseName, areaIds }));
        onClose();
        onSuccess();
        message.info('Successfully updated course!');
      } catch (error) {
        message.error(error?.message || 'An error occurred.');
      }
    },
    [dispatch, course, onSuccess],
  );

  return (
    <>
      <EditOutlined onClick={onOpen} style={{ fontSize: 18 }} />
      <Modal
        title="Update new course"
        visible={isModalVisible}
        onCancel={onClose}
        destroyOnClose
        footer={null}>
        <CourseForm
          hubId={hubId}
          areas={areas}
          initialValues={course}
          onSubmit={handleSubmit}
          onCancel={onClose}
        />
      </Modal>
    </>
  );
}

const CoursesFilters = (props) => {
