  );
};

const CourseStatus = ({ isActive, course, hubId, areas, onSuccess }) => {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);
  areas = areas.concat(course.courseArea);
  const dispatch = useDispatch();
  const onClose = () => setIsModalVisible(false);

  const handleChange = useCallback(async () => {
    try {
      setIsSubmitting(true);
      if (isActive) {
        await dispatch(deleteCourseAsync(course.ID));
        onSuccess();
      } else {
        setIsModalVisible(true);
      }
    } catch {
      message.error('An error occurred.');
    }
    setIsSubmitting(false);
  }, [course, isActive, dispatch]);

  const handleSubmit = useCallback(
    async (formValues) => {
      try {
        const courseId = course.ID;
        const courseName = formValues.COURSE_NAME;
        const areaIds = formValues.courseArea;
        await dispatch(updateCourseAsync({ courseId, courseName, areaIds }));
        onClose();
        onSuccess();
        message.info('Successfully updated course!');
      } catch (error) {
        message.error(error?.message || 'An error occurred.');
      }
    },
    [dispatch, course, onSuccess],
  );

  return (
    <>
      <Switch loading={isSubmitting} checked={isActive} onChange={handleChange} />
      {course.STATUS === 'deleted' && (
        <Modal
          title="Update new course"
          visible={isModalVisible}
          onCancel={onClose}
          destroyOnClose
          footer={null}>
          <CourseForm
            hubId={hubId}
            areas={areas}
            initialValues={course}
            onSubmit={handleSubmit}
            onCancel={onClose}
          />
        </Modal>
      )}
    </>
  );
};

const EditDeviceModal = ({ visible, onOk, onCancel, device, confirmLoading }) => {
  const [form] = Form.useForm();

  useEffect(() => {
    if (!device) return;
    form.setFields([{ name: 'deviceIp', value: device.deviceIp }]);
  }, [device]);

  const onSubmit = () => {
    const fields = form.getFieldsValue();
    onOk({
      ...device,
      deviceIp: fields.deviceIp,
    });
  };

  return (
    <Modal
      title="Edit device IP"
      visible={visible}
      onOk={() => form.submit()}
      onCancel={onCancel}
      destroyOnClose
      confirmLoading={confirmLoading}
      okText="Save"
      cancelButtonProps={{ disabled: confirmLoading }}
      maskClosable={!confirmLoading}
      closable={!confirmLoading}>
      <Form form={form} initialValues={device} onFinish={onSubmit}>
        <Form.Item
          name="deviceIp"
          label="Device IP:"
          rules={[
            { required: true, message: 'Required' },
            {
              pattern: /^(([0-9]|[1-9][0-9]|1[0-9]{2}|2[0-4][0-9]|25[0-5])\.){3}([0-9]|[1-9][0-9]|1[0-9]{2}|2[0-4][0-9]|25[0-5])$/,
              message: 'Invalid IP address',
            },
          ]}>
          <Input
            placeholder="Enter Device IP"
            disabled={confirmLoading}
            autoFocus
            autoComplete="off"
          />
        </Form.Item>
      </Form>
    </Modal>
  );
};

const UpdateAttendanceModal = ({
  open,
  close,
  record,
  refreshAttendanceDetails,
}: UpdateAttendanceModalPropType) => {
  const [remarks, setRemarks] = useState('');
  const [isUpdating, setIsUpdating] = useState(false);

  const router = useRouter();
  const { agentName, agentId } = router.query;

  if (!record) {
    return null;
  }

  const updateAction = async () => {
    setIsUpdating(true);
    try {
      const response = await attendanceAPI.updateAttendanceOfAgent({
        agent_id: Number(agentId),
        date: record.date,
        remarks: isEmpty(remarks.trim()) ? undefined : remarks,
      });
      if (response && response.success) {
        message.success(response.message || 'Attendance updated successfully');
        setRemarks('');
        close();
        refreshAttendanceDetails();
      }
    } catch (err: any) {
      message.error(err.message || 'Failed to update manual attendance');
    }
    setIsUpdating(false);
  };

  const closeModal = () => {
