function DownloadAgentsSummary() {
  const [isSendingPayment, setIsSendingPayment] = useState(false);

  const downloadHubsPayment = () => {
    const fileName = 114;
    const today = new Date();
    const d = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();

    try {
      return _axios
        .get('/v1/download/hub-payment', {
          params: {
            hubId: 114,
            status: 'delivery-payment-collected',
            isC2c: 1,
          },
        })
        .then((res) => {
          generateAndDownloadPdf(res.pdfContent.template, `${fileName}-${d}`);
        });
    } catch (err) {
      message.error('Something went wrong!');
    }
  };

  const handleClick = useCallback(async () => {
    setIsSendingPayment(true);
    await downloadHubsPayment();
    setIsSendingPayment(false);
  }, []);

  return (
    <Button
      onClick={handleClick}
      css={button_css}
      size="medium"
      disabled={isSendingPayment}>
      Agents Summary
    </Button>
  );
}

