function SendPaymentModal({ cashAmt, bkashAmt, onSuccess = () => null }) {
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState(undefined);
  const [isUploading, setIsUploading] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isTransDetailsEmpty, setTransDetailsEmpty] = useState(true);
  const [isPicDepositSlipEmpty, setPicDepositSlipEmpty] = useState(true);
  const [form] = Form.useForm();

  const paymentMethodChange = useCallback((val) => {
    if (val == 'bkash' || val == 'nagad' || val == 'rocket') {
      form.resetFields(['transDetails', 'bankName', 'picDepositSlip']);
      setTransDetailsEmpty(true);
      setPicDepositSlipEmpty(false);
    }
    if (val == 'bank') {
      form.resetFields(['transDetails', 'bankName', 'picDepositSlip']);
      setTransDetailsEmpty(true);
      setPicDepositSlipEmpty(true);
    }
    if (val == 'cash') {
      setTransDetailsEmpty(false);
      setPicDepositSlipEmpty(false);
    }
    setSelectedPaymentMethod(val);
  }, []);

  const onOpen = () => setIsModalVisible(true);

  const onClose = () => {
    form.resetFields();
    setSelectedPaymentMethod(undefined);
    setIsSubmitting(false);
    setIsUploading(false);
    setIsModalVisible(false);
  };

  const handleFileChange = useCallback(async ({ files }) => {
    const file = files?.[0];
    if (!file) {
      setPicDepositSlipEmpty(true);
      return;
    }
    try {
      setIsUploading(true);
      const formData = new FormData();
      formData.append('doc', file);
      const data = await uploadAPI.uploadBankDepositSlip(formData);
      form.setFieldsValue({ ...form.getFieldValue(), attachmentUrl: data.fileUrl });
      setIsUploading(false);
      setPicDepositSlipEmpty(false);
    } catch (err) {
      message.error('Failed to upload document.');
      setPicDepositSlipEmpty(true);
    }
  }, []);

  const handlePopupCancel = () => {
    setIsSubmitting(false);
  };

  const onSubmit = useCallback(async () => {
    setIsSubmitting(true);
    const values = form.getFieldValue();
    if (values.transDetails) {
      values.transDetails = values.transDetails.trim();
    }
    const payload = {
      status: 'delivery-payment-sent',
      oldStatus: 'delivery-payment-collected',
      action: 'payment-sent-to-central-hub',
      currentHubId: 114,
      sourceHubId: 114,
      destinationHubId: 8,
      paymentMethod: values.paymentMethod,
      transactionNumber: values.transDetails,
      attachmentUrl: values.attachmentUrl || undefined,
      amount: bkashAmt + cashAmt,
      isC2c: true,
    };

